   Num numero = new Num();
    private void btexcluirActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btexcluirActionPerformed
           numero.excluir(Integer.parseInt(edCoD.getText()));
      
       btListar.doClick();     
         
      
    }//GEN-LAST:event_btexcluirActionPerformed

    private void btsalvarNumActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btsalvarNumActionPerformed
    numero.salvar(Integer.parseInt(ednumero.getText()));
     btListar.doClick();     
     ednumero.setText(null);
    
    }//GEN-LAST:event_btsalvarNumActionPerformed

