    private void btListarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btListarActionPerformed
       atlistaN.setText(numero.getNum());
    }//GEN-LAST:event_btListarActionPerformed

    private void btBuscarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btBuscarActionPerformed
      int n = numero.buscar(Integer.parseInt(edbuscar.getText()));
        atlistaN.setText(String.valueOf(n));
    }//GEN-LAST:event_btBuscarActionPerformed

    private void btparActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btparActionPerformed
         atlistaN.setText(numero.listarPares());
    }//GEN-LAST:event_btparActionPerformed

    private void btimparActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btimparActionPerformed
        atlistaN.setText(numero.listarImpares());
    }//GEN-LAST:event_btimparActionPerformed

    private void btmaximoActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btmaximoActionPerformed
     int maximo=numero.getNumeroMaximo();
    JOptionPane.showMessageDialog(null, "Número Máximo: " + maximo);
    }//GEN-LAST:event_btmaximoActionPerformed

    private void btminimoActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btminimoActionPerformed
         int minimo = numero.getNumeroMinimo();
    JOptionPane.showMessageDialog(null, "Número Mínimo: " + minimo);
    }//GEN-LAST:event_btminimoActionPerformed

    private void btOrdenarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btOrdenarActionPerformed
    numero.ordenarLista();
        btListar.doClick();
    }//GEN-LAST:event_btOrdenarActionPerformed

